import React from 'react';
import './HeroSection.css';

const HeroSection = () => {
  return (
    <div className="hero-container">
      {/* Left sidebar with title */}
        <div className="sidebar">
          <h1 className="main-title">MC Movers Payment Portal</h1>
          <p className="tagline">Secure payment options for your moving deposit</p>
          <p className="subtitle">Choose your preferred payment method below</p>
        </div>

      {/* Hero section with payment options */}
      <div className="hero-section">
        <div className="hero-image">
          <div className="hero-text">
            <h2>MC Movers Makes Moving Easy</h2>
            <p>Welcome to MC Movers' secure payment portal. We've created this website to make paying your moving deposit simple and stress-free. Choose from our convenient payment options below and follow the step-by-step instructions for each method.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
